void VMMain(int argc, char *argv[]){
    TVMThreadID VMThreadIDMedium1, VMThreadIDMedium2, VMThreadIDMedium3, VMThreadIDMedium4, VMThreadIDMedium5, VMThreadIDMedium6, VMThreadIDMedium7, VMThreadIDMedium8, VMThreadIDMedium9;
 
    VMPrint("VMMain creating threads.\n");
    VMThreadCreate(VMThreadMedium1, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium1);
    VMThreadCreate(VMThreadMedium2, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium2);
    VMThreadCreate(VMThreadMedium3, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium3);
    VMThreadCreate(VMThreadMedium4, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium4);
    VMThreadCreate(VMThreadMedium5, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium5);
    VMThreadCreate(VMThreadMedium6, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium6);
    VMThreadCreate(VMThreadMedium7, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium7);
    VMThreadCreate(VMThreadMedium8, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium8);
    VMThreadCreate(VMThreadMedium9, NULL, 0x100000, VM_THREAD_PRIORITY_NORMAL, &VMThreadIDMedium9);
 
    VMThreadActivate(VMThreadIDMedium1);
    VMThreadActivate(VMThreadIDMedium2);
    VMThreadActivate(VMThreadIDMedium3);
    VMThreadActivate(VMThreadIDMedium4);
    VMThreadActivate(VMThreadIDMedium5);
    VMThreadActivate(VMThreadIDMedium6);
    VMThreadActivate(VMThreadIDMedium7);
    VMThreadActivate(VMThreadIDMedium8);
    VMThreadActivate(VMThreadIDMedium9);
 
    VMPrint("\n");
    VMPrint("Goodbye (This will not be the very last line.)\n");
}